$platypus = Compute("platypus")->cmp(checker => sub {
  my ($correct,$student,$ans) = @_;
  $correct = $correct->value;
  $student = $student->value;
  $student = lc($student);
  $student =~ s/ +/ /;
  $student =~ s/^ //;
  $student =~ s/ $//;
  return ($student eq $correct);
});
