    }

Configuration |=
    element rename {
        attribute element {text},
        attribute xml:lang {text}?,
        text
    }

Configuration |=
    element images {
        element archive {
            attribute from {text}?,
            text
        }+
    }

Configuration |=
    element author-biographies {
        attribute length {"short" | "long"}
    }

Configuration |=
    element numbering {
        element division {
            attribute part {"decorative" | "structural"}
        }?
    }

Demonstration = element demonstration {
    Title,
    Paragraph,
    Sage
}
