start =
    element pretext {
        XMLLang?,
        DocInfo?,
        (Book | Article | Letter | Memorandum)
    }

Paragraphs =
    element paragraphs {
        MetaDataTitle,
        Index*,
        BlockDivision+
    }
ParagraphsNoNumber =
    element paragraphs {
        MetaDataTitle,
        Index*,
        BlockStatementNoCaption+
    }
Commentary =
    element commentary {
        MetaDataTitle,
        Index*,
        BlockStatementNoCaption+
    }

FillIn = element fillin {attribute characters {xsd:integer}?, empty}
Character |=
    FillIn

