    }

IdxHeading =
    element h {
        attribute sortby {text}?,
        TextShort
    }
Index =
    element idx {
        MetaDataTarget,
        attribute sortby {text}?,
        attribute start {text}?,
        attribute finish {text}?,
        (
            TextShort
        |
            (
            IdxHeading,
            IdxHeading?,
            IdxHeading?,
            (element see {TextShort} | element seealso {TextShort})?
            )
        )
    }
IndexList = element index-list {empty}

