List =
    element ol {
        PermanentID?,
        attribute cols {"2"|"3"|"4"|"5"|"6"}?,
        attribute label {text}?,
        element li {
            MetaDataTarget,
            (TextParagraph | BlockStatement+)
        }+
    }
|
    element ul {
        PermanentID?,
        attribute cols {"2"|"3"|"4"|"5"|"6"}?,
        attribute label {"disc" | "circle" | "square" | ""}?,
        element li {
            PermanentID?,
            (TextParagraph | BlockStatement+)
        }+
    }
|
    element dl {
        PermanentID?,
        attribute width {"narrow" | "medium" | "wide"}?,
        element li {
            MetaDataTitle,
            BlockStatement+
        }+
    }

