    element list-of {
        attribute elements {text},
        attribute scope {text}?,
        attribute divisions {text}?,
        attribute empty {"yes" | "no"}?
    }
NotationList =
    element notation-list {empty}

ArticleFrontMatter =
    element frontmatter {
        MetaDataTitleOptional,
        TitlePage,
        Abstract?
    }
BookFrontMatter = element frontmatter {
        MetaDataTitleOptional,
        TitlePage?,
        ColophonFront?,
        Biography*,
        Dedication?,
        Acknowledgement?,
        Preface*
    }
TitlePage =
    element titlepage {
        (
            (Author, Author*, Editor*)
            |
            (Editor, Editor*)
        ),
        Credit*,
        Date?
    }
Author =
    element author {
        element personname {TextSimple},
        element department {TextSimple | ShortLine+}?,
        element institution {TextSimple | ShortLine+}?,
        element email {text}?
    }
Editor =
    element editor {
        element personname {TextSimple},
        element department {TextSimple | ShortLine+}?,
        element institution {TextSimple | ShortLine+}?,
        element email {text}?
    }
Credit =
    element credit {
        element title {TextLong},
        Author+
    }
Date =
    element date {
        mixed {(Character | Generator)*}
    }
Abstract =
    element abstract {
        MetaDataTitleOptional,
        BlockText+
    }
ColophonFront =
    element colophon {
        MetaDataTarget,
        element credit {
            element role {TextShort},
            element entity {TextLong}
        }*,
        element edition {text}?,
        element website {
            element name {TextShort},
            element address {text}
        }?,
        element copyright {
            element year {TextShort},
            element holder {text},
            element minilicense {TextShort}?,
            element shortlicense {TextLong}?
        }?
    }
Biography =
    element biography {
        MetaDataTitleOptional,
        (BlockStatementNoCaption | ParagraphsNoNumber | Commentary)+
    }
Dedication =
    element dedication {
        MetaDataTitleOptional,
        (Paragraph|ParagraphLined)+
    }
Acknowledgement =
    element acknowledgement {
        MetaDataTitleOptional,
        (BlockStatementNoCaption | ParagraphsNoNumber | Commentary)+
    }
Preface =
    element preface {
        MetaDataTitleOptional,
        (
            (
                (BlockStatementNoCaption | ParagraphsNoNumber | Commentary)+,
                Attribution*
            )
            |
            (
                (BlockStatementNoCaption | ParagraphsNoNumber | Commentary)*,
                Contributors,
                (BlockStatementNoCaption | ParagraphsNoNumber | Commentary)*
            )
        )
    }

